  def('encaseEither2',
      {},
      [$.Function, $.Function, a, b, $Either(l, r)],
      function(f, g, x, y) {
        try {
          return Right(g(x)(y));
        } catch (err) {
          return Left(f(err));
        }
      });

  //# encaseEither2_ :: (Error -> l) -> ((a, b) -> r) -> a -> b -> Either l r
