  def('or',
      {a: [Alternative]},
      [a, a, a],
      function(x, y) { return toBoolean(x) ? x : y; });

  //# xor :: (Alternative a, Monoid a) => a -> a -> a
  //.
  //. Takes two values of the same type and returns the "true" value
